import React, { useState } from 'react';
import { Row, Col, Table, Card, Button } from 'react-bootstrap';

const Checkbox = ({ id, type, handleClick, isChecked }) => {
    return (
      <input
        id={id}
        type={type}
        onChange={handleClick}
        checked={isChecked}
      />
    );
};

const Notifications = () => {
    const [isCheckAll, setIsCheckAll] = useState(false);
    const [isCheck, setIsCheck] = useState([]);

    const handleSelectAll = e => {
        setIsCheckAll(!isCheckAll);
        setIsCheck(list.map(li => li.id));
        if (isCheckAll) {
            setIsCheck([]);
        }
    };
    
    const handleClick = e => {
        const { id, checked } = e.target;
        setIsCheck([...isCheck, id]);
        if (!checked) {
            setIsCheck(isCheck.filter(item => item !== id));
        }
    };

    console.log(isCheck);

    return (
        <React.Fragment>
            <Card>
                <Card.Body>
                    <Row>
                        <Col xl={12}>
                            <Button variant="secondary" style={{float: "left"}}>Marcar como lido{" "}<i className="feather icon-check" style={{margin:"0"}}/></Button>
                            <Button variant="secondary" style={{float: "left"}}>Excluir{" "}<i className="feather icon-trash" style={{margin:"0"}}/></Button>
                        </Col>
                        <Col xl={12}>
                        <Table responsive>
                                <tbody>
                                    <tr className="unread">
                                        <td className="col-xl-2">
                                            <Checkbox 
                                                type="checkbox"
                                                id="selectAll"
                                                handleClick={handleSelectAll}
                                                isChecked={isCheckAll}
                                            />
                                        </td>
                                        <td className="col-xl-10">Mensagem</td>
                                    </tr>
                                </tbody>
                            </Table>
                        </Col>
                        <Col xl={12}>
                            <Table responsive>
                                <tbody>
                                    <tr className="unread">
                                        <td className="col-xl-2">
                                            <Checkbox 
                                                type="checkbox"
                                                id="01"
                                                handleClick={handleClick}
                                                isChecked={isCheck.includes("01")}
                                            />
                                        </td>
                                        <td className="col-xl-10">
                                            Robert Fox adicionou você como seu médico!
                                        </td>
                                    </tr>
                                    <tr className="unread">
                                        <td className="col-xl-2">
                                            <Checkbox 
                                                type="checkbox"
                                                id="02"
                                                handleClick={handleClick}
                                                isChecked={isCheck.includes("02")}
                                            />
                                        </td>
                                        <td className="col-xl-10">
                                            Darlene Robertson adicionou você como seu médico!
                                        </td>
                                    </tr>
                                </tbody>
                            </Table>
                        </Col>
                    </Row>
                </Card.Body>
            </Card>
        </React.Fragment>
    );
};

export default Notifications;
